local ngx_log = ''

ngx = {
    worker = {
        pid = function() return 153 end,
        exiting = function() end
    },
    ERR = 0,
    WARN = 1,
    INFO = 2,
    DEBUG = 3,
    log = function(level, input)
        if level == ngx.ERR then
            ngx_log = input
        end
    end,
    sleep = function() end,
    timer = {
        at = function(delay, callback, ...)
            -- store timer callback with arguments
            local args = {...}
            scheduled_timers[#scheduled_timers+1] = function()
                callback(false, unpack(args))
            end
            return true
        end
    },
    req = {
        get_headers = function()
            local headers = {}
            headers['X-Ya-Service-Ticket'] = service_ticket
            headers['X-Client-Id-Header'] = 'client_from_header'
            return headers
        end
    },
    HTTP_FORBIDDEN = 403,
    HTTP_INTERNAL_SERVER_ERROR = 500,
    say = function() end,
    exit = function() end,
    status = 200,
    header = {},
    var = {}
}

-- mock resty.http
local quota_response = nil
local http_log = ''
local quota_get_failure = false

package.loaded['resty.http'] = {
    new = function()
        return {
            connect = function() return true end,
            request = function(self, params)
                http_log = http_log .. params.method .. '(' .. params.path .. ')'
                -- mock responses
                if params.path == '/quota' then
                    assert(params.headers['Content-Type'] == 'application/flatbuffer')
                    if quota_get_failure == true then
                        return {
                            status = 500,
                            read_body = function()
                                return 'Internal server error'
                            end
                        }, 'TEST ERROR'
                    end
                    return {
                        status = 200,
                        read_body = function()
                            return quota_response
                        end
                    }
                elseif params.path == '/tvmkeys' then
                    return {
                        status = 200,
                        read_body = function() return tvm_unittest_public_keys end
                    }
                elseif params.path == '/configs' then
                    return {
                        status = 200,
                        read_body = function()
                            -- pre-serialized configs:
                            --   TVM_ID = 123
                            --   TVM_ENABLED = 1
                            return '\12\0\0\0\0\0\6\0\8\0\4\0\6\0\0\0\4\0\0\0'..
                                    '\2\0\0\0\48\0\0\0\4\0\0\0\224\255\255\255'..
                                    '\16\0\0\0\4\0\0\0\3\0\0\0\49\50\51\0\6\0'..
                                    '\0\0\84\86\77\95\73\68\0\0\8\0\12\0\4\0'..
                                    '\8\0\8\0\0\0\16\0\0\0\4\0\0\0\1\0\0\0\49'..
                                    '\0\0\0\11\0\0\0\84\86\77\95\69\78\65\66'..
                                    '\76\69\68\0' end
                    }
                end

            end,
            set_timeout = function() end,
            set_keepalive = function() end
        }
    end
}

-- Initialization and background jobs tests

local plugin = require("rate-limiter-nginx-plugin")

-- On load plugin schedules background jobs initialization with ngx.timer.at(0)
-- So right after start expect single timer registered
assert(#scheduled_timers == 1)
local run_callbacks = scheduled_timers
scheduled_timers = {}
-- run timer callbacks
for i = 1, #run_callbacks do run_callbacks[i]() end
assert(http_log == 'GET(/configs)GET(/tvmkeys)')
http_log = ''

-- expect 3 background jobs scheduled as timers: configs update, tvmkeys update
assert(#scheduled_timers == 2)
run_callbacks = scheduled_timers
scheduled_timers = {}

-- expect all jobs auto reschedule themselves each cycle
for i = 1, #run_callbacks do run_callbacks[i]() end
assert(#scheduled_timers == 2)

-- expect one more /quotas request
assert(http_log == 'GET(/configs)GET(/tvmkeys)')
http_log = ''

-- turn on handler printing on reject
plugin.SET_OPERATION_ID_VAR_ON_REJECT = true

-- rate_access tests

local function check_status(status)
    if ngx.status ~= status then
        print('Wrong status: ' .. status .. ', expected: ' .. ngx.status)
        print(plugin.get_internal_state())
    end
    assert(ngx.status == status)
    if ngx_log:find('Limit EXCEEDED') ~= nil or ngx_log:find('TEST ERROR') ~= nil then
        ngx_log = ''
    else
        if ngx_log ~= '' then
            print('Errors in ngx.log: ' .. ngx_log)
        end
        assert(ngx_log == '')
    end
end

quota_response = utils.make_mock_body('client', 'resource.1', 5, false, 'my_handler_name')
-- allowed
plugin.rate_access('client', 'resource.1', 1)
check_status(200)
assert(ngx.var.upstream_http_x_yataxi_api_operationid == nil)

quota_response = utils.make_mock_body('client', 'resource.1', 0, false, 'my_handler_name')
-- limit exceeded
plugin.rate_access('client', 'resource.1', 5)
check_status(429)
assert(ngx.header['Content-Type'] == 'text/plain')
assert(ngx.var.upstream_http_x_yataxi_api_operationid == 'my_handler_name')

quota_response = utils.make_mock_body('client', 'resource.forbidden', -1)
-- access forbidden
plugin.rate_access('client', 'resource.forbidden', 1)
check_status(ngx.HTTP_FORBIDDEN)

