check_status(200)
quota_get_failure = false

-- test regexp resources
quota_response = utils.make_mock_body('regex_client', [[/test/handler/[\\w]+]], 3, true)

plugin.rate_access('regex_client', '/test/handler/test1', 1)
check_status(200)

-- no budget for future requests
quota_response = utils.make_mock_body('regex_client', [[/test/handler/[\\w]+]], 0, true)

-- spend whole quota
plugin.rate_access('regex_client', '/test/handler/test2', 1)
check_status(200)
plugin.rate_access('regex_client', '/test/handler/test3', 1)
check_status(200)

-- /quota request returns 0, reject
plugin.rate_access('regex_client', '/test/handler/test4', 1)
check_status(429)

-- test fallback quota is applied
plugin.invalidate()
ngx.status = 200
quota_response = utils.make_mock_body('', [[/v1/cached-value/.+]], 1, true)
plugin.rate_access('client2', '/v1/cached-value/test3', 1)
check_status(200)

quota_get_failure = true
plugin.rate_access('client2', '/v1/cached-value/test3', 1)
check_status(200)
quota_get_failure = false
plugin.invalidate()

-- test extract client_id from custom header
plugin.CUSTOM_HEADER_WITH_CLIENT_ID = 'X-Client-Id-Header'

quota_response = utils.make_mock_body('client_from_header', 'resource.custom_header', 1)
ngx.status = 200
plugin.tvm_rate_access('resource.custom_header', 1)
check_status(200)

quota_response = utils.make_mock_body('client_from_header', 'resource.custom_header', 0)
plugin.tvm_rate_access('resource.custom_header', 1)
check_status(429)

plugin.CUSTOM_HEADER_WITH_CLIENT_ID = nil
